// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [croppedImage, setCroppedImage] = useState(null);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  const [coordinates, setCoordinates] = useState({
    minX: 0,
    maxX: 0,
    minY: 0,
    maxY: 0
  });
  const canvasRef = useRef(null);

  // Draw rectangle on image
  const drawRectangle = (img) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size to match image
    canvas.width = img.width;
    canvas.height = img.height;

    // Draw the image
    ctx.drawImage(img, 0, 0);

    // Draw rectangle
    ctx.strokeStyle = 'red';
    ctx.lineWidth = 2;
    ctx.strokeRect(
      coordinates.minX,
      coordinates.minY,
      coordinates.maxX - coordinates.minX,
      coordinates.maxY - coordinates.minY
    );
  };

  // Update rectangle when coordinates change
  useEffect(() => {
    if (image) {
      const img = new Image();
      img.src = image;
      img.onload = () => {
        drawRectangle(img);
        setImageDimensions({ width: img.width, height: img.height });
      };
    }
  }, [coordinates, image]);

  // Handle coordinate changes
  const handleCoordinateChange = (e) => {
    const { name, value } = e.target;
    setCoordinates(prev => ({
      ...prev,
      [name]: parseInt(value) || 0
    }));
  };

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
      setCroppedImage(null); // Reset cropped image
      setText(''); // Reset text
    }
  };

  // Crop image using coordinates
  const cropImage = (imageElement) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Set canvas size to the specified dimensions
    canvas.width = coordinates.maxX - coordinates.minX;
    canvas.height = coordinates.maxY - coordinates.minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      coordinates.minX, coordinates.minY,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY,
      0, 0,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY
    );

    return canvas.toDataURL('image/png');
  };

  // Process image and run OCR
  const handleRunOCR = () => {
    if (!image) {
      alert('Please upload an image first');
      return;
    }

    setIsLoading(true);
    setText('');

    const img = new Image();
    img.src = image;

    img.onload = () => {
      const croppedImageData = cropImage(img);
      setCroppedImage(croppedImageData);

      Tesseract.recognize(
        croppedImageData,
        'eng',
        {
          logger: (m) => console.log(m)
        }
      )
        .then(({ data: { text } }) => {
          setText(text);
          setIsLoading(false);
        })
        .catch((error) => {
          console.error(error);
          setIsLoading(false);
        });
    };
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
        />
      </div>

      {/* Coordinate Inputs */}
      <div className="coordinates-input">
        <h3>Enter Coordinates</h3>
        <div className="coordinate-group">
          <div>
            <label>Min X:</label>
            <input
              type="number"
              name="minX"
              value={coordinates.minX}
              onChange={handleCoordinateChange}
              placeholder="Min X"
            />
          </div>
          <div>
            <label>Max X:</label>
            <input
              type="number"
              name="maxX"
              value={coordinates.maxX}
              onChange={handleCoordinateChange}
              placeholder="Max X"
            />
          </div>
          <div>
            <label>Min Y:</label>
            <input
              type="number"
              name="minY"
              value={coordinates.minY}
              onChange={handleCoordinateChange}
              placeholder="Min Y"
            />
          </div>
          <div>
            <label>Max Y:</label>
            <input
              type="number"
              name="maxY"
              value={coordinates.maxY}
              onChange={handleCoordinateChange}
              placeholder="Max Y"
            />
          </div>
        </div>
      </div>

      {/* Run OCR Button */}
      {image && (
        <div className="run-ocr">
          <button onClick={handleRunOCR} disabled={isLoading}>
            {isLoading ? 'Processing...' : 'Run OCR'}
          </button>
        </div>
      )}

      {/* Image Previews Container */}
      {image && (
        <div className="image-preview-container">
          {/* Original Image with Rectangle */}
          <div className="image-preview">
            <h2>Original Image:</h2>
            <canvas ref={canvasRef} style={{ maxWidth: '400px' }} />
            <div className="image-dimensions">
              Size: {imageDimensions.width} x {imageDimensions.height} pixels
            </div>
          </div>

          {/* Cropped Image Preview */}
          {croppedImage && (
            <div className="image-preview">
              <h2>Cropped Image:</h2>
              <img src={croppedImage} alt="Cropped" style={{ maxWidth: '400px' }} />
            </div>
          )}

          {/* Extracted Text */}
          {text && (
            <div className="result">
              <h2>Extracted Text:</h2>
              <pre>{text}</pre>
            </div>
          )}
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}
    </div>
  );
}

export default App;
