// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [images, setImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [batchProgress, setBatchProgress] = useState({ current: 0, total: 0 });
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  const [coordinates, setCoordinates] = useState({
    minX: 0,
    maxX: 0,
    minY: 0,
    maxY: 0
  });
  const [regionName, setRegionName] = useState('Region 1');
  const canvasRef = useRef(null);

  // Draw rectangle on image
  const drawRectangle = (img) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size to match image
    canvas.width = img.width;
    canvas.height = img.height;

    // Draw the image
    ctx.drawImage(img, 0, 0);

    // Get regions for current image
    const regions = images[currentImageIndex]?.regions || [];

    // Draw all regions
    regions.forEach((region, index) => {
      // Different color for each region
      const colors = ['red', 'blue', 'green', 'orange', 'purple', 'yellow', 'cyan'];
      ctx.strokeStyle = colors[index % colors.length];
      ctx.lineWidth = 2;
      ctx.strokeRect(
        region.coordinates.minX,
        region.coordinates.minY,
        region.coordinates.maxX - region.coordinates.minX,
        region.coordinates.maxY - region.coordinates.minY
      );

      // Add region name
      ctx.fillStyle = colors[index % colors.length];
      ctx.font = '12px Arial';
      ctx.fillText(
        region.name || `Region ${index + 1}`,
        region.coordinates.minX,
        region.coordinates.minY - 5
      );
    });

    // Draw current region being edited
    if (!regions.some(r => r.isEditing)) {
      ctx.strokeStyle = 'red';
      ctx.lineWidth = 2;
      ctx.strokeRect(
        coordinates.minX,
        coordinates.minY,
        coordinates.maxX - coordinates.minX,
        coordinates.maxY - coordinates.minY
      );
    }
  };

  // Update rectangle when coordinates change
  useEffect(() => {
    if (images[currentImageIndex]) {
      const img = new Image();
      img.src = images[currentImageIndex].url;
      img.onload = () => {
        drawRectangle(img);
        setImageDimensions({ width: img.width, height: img.height });
      };
    }
  }, [coordinates, currentImageIndex, images]);

  // Handle coordinate changes
  const handleCoordinateChange = (e) => {
    const { name, value } = e.target;
    setCoordinates(prev => ({
      ...prev,
      [name]: parseInt(value) || 0
    }));
  };

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files) {
      const newImages = Array.from(e.target.files).map(file => ({
        url: URL.createObjectURL(file),
        regions: [],
        text: ''
      }));
      setImages(prev => [...prev, ...newImages]);
      setCurrentImageIndex(prev => prev + newImages.length - 1);
    }
  };

  // Handle image selection
  const handleImageSelect = (index) => {
    setCurrentImageIndex(index);
    setText(images[index].text || '');

    // Reset coordinate values when switching images
    setCoordinates({
      minX: 0,
      maxX: 0,
      minY: 0,
      maxY: 0
    });
    setRegionName(`Region ${(images[index].regions?.length || 0) + 1}`);
  };

  // Crop image using coordinates
  const cropImage = (imageElement, coords) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Set canvas size to the specified dimensions
    canvas.width = coords.maxX - coords.minX;
    canvas.height = coords.maxY - coords.minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      coords.minX, coords.minY,
      coords.maxX - coords.minX, coords.maxY - coords.minY,
      0, 0,
      coords.maxX - coords.minX, coords.maxY - coords.minY
    );

    return canvas.toDataURL('image/png');
  };

  // Add a new region
  const addRegion = () => {
    if (!images[currentImageIndex]) return;

    // Validate coordinates
    if (coordinates.minX >= coordinates.maxX || coordinates.minY >= coordinates.maxY) {
      alert('Invalid coordinates. Make sure Max X > Min X and Max Y > Min Y');
      return;
    }

    const newRegion = {
      name: regionName,
      coordinates: { ...coordinates },
      text: '',
      croppedImageData: null
    };

    setImages(prev => {
      const newImages = [...prev];
      if (!newImages[currentImageIndex].regions) {
        newImages[currentImageIndex].regions = [];
      }
      newImages[currentImageIndex].regions.push(newRegion);
      return newImages;
    });

    // Reset for next region
    setRegionName(`Region ${(images[currentImageIndex].regions?.length || 0) + 2}`);
  };

  // Remove a region
  const removeRegion = (regionIndex) => {
    setImages(prev => {
      const newImages = [...prev];
      newImages[currentImageIndex].regions.splice(regionIndex, 1);
      return newImages;
    });
  };

  // Edit a region
  const editRegion = (regionIndex) => {
    const region = images[currentImageIndex].regions[regionIndex];
    setCoordinates(region.coordinates);
    setRegionName(region.name);

    // Mark as editing
    setImages(prev => {
      const newImages = [...prev];
      newImages[currentImageIndex].regions = newImages[currentImageIndex].regions.map(
        (r, idx) => ({ ...r, isEditing: idx === regionIndex })
      );
      return newImages;
    });
  };

  // Update a region
  const updateRegion = (regionIndex) => {
    setImages(prev => {
      const newImages = [...prev];
      newImages[currentImageIndex].regions[regionIndex] = {
        ...newImages[currentImageIndex].regions[regionIndex],
        name: regionName,
        coordinates: { ...coordinates },
        isEditing: false
      };
      return newImages;
    });

    // Reset for next region
    setCoordinates({
      minX: 0,
      maxX: 0,
      minY: 0,
      maxY: 0
    });
    setRegionName(`Region ${(images[currentImageIndex].regions?.length || 0) + 1}`);
  };

  // Process single image with OCR
  const processImageOCR = (imageUrl, regions) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.src = imageUrl;

      img.onload = async () => {
        try {
          const updatedRegions = await Promise.all(
            regions.map(async (region) => {
              const croppedImageData = cropImage(img, region.coordinates);

              const result = await Tesseract.recognize(
                croppedImageData,
                'eng',
                {
                  logger: (m) => console.log(m)
                }
              );

              return {
                ...region,
                text: result.data.text,
                croppedImageData
              };
            })
          );

          resolve(updatedRegions);
        } catch (error) {
          reject(error);
        }
      };
    });
  };

  // Process image and run OCR
  const handleRunOCR = async () => {
    const currentImage = images[currentImageIndex];
    if (!currentImage || !currentImage.regions || currentImage.regions.length === 0) {
      alert('Please add at least one region first');
      return;
    }

    setIsLoading(true);

    try {
      const updatedRegions = await processImageOCR(
        currentImage.url, 
        currentImage.regions
      );

      setImages(prev => {
        const newImages = [...prev];
        newImages[currentImageIndex].regions = updatedRegions;
        return newImages;
      });

      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  // Process all images with OCR
  const handleRunOCRAll = async () => {
    const imagesWithRegions = images.filter(img => img.regions && img.regions.length > 0);

    if (imagesWithRegions.length === 0) {
      alert('Please add at least one region to at least one image first');
      return;
    }

    setIsLoading(true);
    setBatchProgress({ current: 0, total: imagesWithRegions.length });

    try {
      const updatedImages = await Promise.all(
        images.map(async (img, index) => {
          if (!img.regions || img.regions.length === 0) {
            return img;
          }

          const updatedRegions = await processImageOCR(img.url, img.regions);
          setBatchProgress(prev => ({ ...prev, current: prev.current + 1 }));

          return {
            ...img,
            regions: updatedRegions
          };
        })
      );

      setImages(updatedImages);
      setIsLoading(false);
      setBatchProgress({ current: 0, total: 0 });
    } catch (error) {
      console.error(error);
      setIsLoading(false);
      setBatchProgress({ current: 0, total: 0 });
    }
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageChange}
        />
      </div>

      {/* Image Selection */}
      {images.length > 0 && (
        <div className="image-selection">
          <h3>Select Image:</h3>
          <div className="image-thumbnails">
            {images.map((img, index) => (
              <div 
                key={index}
                className={`image-thumbnail ${index === currentImageIndex ? 'selected' : ''}`}
                onClick={() => handleImageSelect(index)}
              >
                <img src={img.url} alt={`Thumbnail ${index + 1}`} />
                <span>Image {index + 1}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Regions Management */}
      {images.length > 0 && (
        <div className="regions-management">
          <h3>Regions</h3>

          {/* Region List */}
          {images[currentImageIndex]?.regions?.length > 0 && (
            <div className="region-list">
              {images[currentImageIndex].regions.map((region, index) => (
                <div key={index} className="region-item">
                  <span>{region.name}</span>
                  <div className="region-actions">
                    {region.isEditing ? (
                      <button onClick={() => updateRegion(index)}>Update</button>
                    ) : (
                      <button onClick={() => editRegion(index)}>Edit</button>
                    )}
                    <button onClick={() => removeRegion(index)}>Remove</button>
                  </div>
                  {region.text && (
                    <div className="region-text">
                      <strong>Text:</strong> {region.text.substring(0, 50)}
                      {region.text.length > 50 ? '...' : ''}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}

          {/* Coordinate Inputs */}
          <div className="coordinates-input">
            <h4>{images[currentImageIndex]?.regions?.some(r => r.isEditing) 
              ? 'Edit Region' 
              : 'Add New Region'}</h4>

            <div className="region-name-input">
              <label>Region Name:</label>
              <input
                type="text"
                value={regionName}
                onChange={(e) => setRegionName(e.target.value)}
                placeholder="Region Name"
              />
            </div>

            <div className="coordinate-group">
              <div>
                <label>Min X:</label>
                <input
                  type="number"
                  name="minX"
                  value={coordinates.minX}
                  onChange={handleCoordinateChange}
                  placeholder="Min X"
                />
              </div>
              <div>
                <label>Max X:</label>
                <input
                  type="number"
                  name="maxX"
                  value={coordinates.maxX}
                  onChange={handleCoordinateChange}
                  placeholder="Max X"
                />
              </div>
              <div>
                <label>Min Y:</label>
                <input
                  type="number"
                  name="minY"
                  value={coordinates.minY}
                  onChange={handleCoordinateChange}
                  placeholder="Min Y"
                />
              </div>
              <div>
                <label>Max Y:</label>
                <input
                  type="number"
                  name="maxY"
                  value={coordinates.maxY}
                  onChange={handleCoordinateChange}
                  placeholder="Max Y"
                />
              </div>
            </div>

            {images[currentImageIndex]?.regions?.some(r => r.isEditing) ? (
              <button onClick={() => {
                const index = images[currentImageIndex].regions.findIndex(r => r.isEditing);
                if (index !== -1) updateRegion(index);
              }}>
                Update Region
              </button>
            ) : (
              <button onClick={addRegion}>Add Region</button>
            )}
          </div>
        </div>
      )}

      {/* OCR Buttons */}
      {images.length > 0 && (
        <div className="run-ocr">
          <button onClick={handleRunOCR} disabled={isLoading}>
            {isLoading ? 'Processing...' : 'Run OCR on Current Image'}
          </button>
          <button onClick={handleRunOCRAll} disabled={isLoading} className="run-all-button">
            {isLoading ? `Processing ${batchProgress.current}/${batchProgress.total}...` : 'Run OCR on All Images'}
          </button>
        </div>
      )}

      {/* Image Previews Container */}
      {images.length > 0 && (
        <div className="image-preview-container">
          {/* Original Image with Rectangles */}
          <div className="image-preview">
            <h2>Original Image:</h2>
            <canvas ref={canvasRef} style={{ maxWidth: '400px' }} />
            <div className="image-dimensions">
              Size: {imageDimensions.width} x {imageDimensions.height} pixels
            </div>
          </div>

          {/* Results */}
          {images[currentImageIndex]?.regions?.length > 0 && (
            <div className="regions-results">
              <h2>Region Results:</h2>

              <div className="regions-grid">
                {images[currentImageIndex].regions.map((region, index) => (
                  <div key={index} className="region-result">
                    <h3>{region.name}</h3>

                    {region.croppedImageData && (
                      <div className="region-image">
                        <img 
                          src={region.croppedImageData} 
                          alt={`${region.name} cropped`} 
                          style={{ maxWidth: '200px' }} 
                        />
                      </div>
                    )}

                    {region.text && (
                      <div className="region-text-result">
                        <h4>Extracted Text:</h4>
                        <pre>{region.text}</pre>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}
    </div>
  );
}

export default App;
