// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [images, setImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  const [coordinates, setCoordinates] = useState({
    minX: 0,
    maxX: 0,
    minY: 0,
    maxY: 0
  });
  const canvasRef = useRef(null);

  // Draw rectangle on image
  const drawRectangle = (img) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size to match image
    canvas.width = img.width;
    canvas.height = img.height;

    // Draw the image
    ctx.drawImage(img, 0, 0);

    // Draw rectangle
    ctx.strokeStyle = 'red';
    ctx.lineWidth = 2;
    ctx.strokeRect(
      coordinates.minX,
      coordinates.minY,
      coordinates.maxX - coordinates.minX,
      coordinates.maxY - coordinates.minY
    );
  };

  // Update rectangle when coordinates change
  useEffect(() => {
    if (images[currentImageIndex]) {
      const img = new Image();
      img.src = images[currentImageIndex].url;
      img.onload = () => {
        drawRectangle(img);
        setImageDimensions({ width: img.width, height: img.height });
      };
    }
  }, [coordinates, currentImageIndex, images]);

  // Handle coordinate changes
  const handleCoordinateChange = (e) => {
    const { name, value } = e.target;
    setCoordinates(prev => ({
      ...prev,
      [name]: parseInt(value) || 0
    }));
  };

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files) {
      const newImages = Array.from(e.target.files).map(file => ({
        url: URL.createObjectURL(file),
        coordinates: { minX: 0, maxX: 0, minY: 0, maxY: 0 },
        text: ''
      }));
      setImages(prev => [...prev, ...newImages]);
      setCurrentImageIndex(prev => prev + newImages.length - 1);
    }
  };

  // Handle image selection
  const handleImageSelect = (index) => {
    setCurrentImageIndex(index);
    setCoordinates(images[index].coordinates);
    setText(images[index].text);
  };

  // Crop image using coordinates
  const cropImage = (imageElement) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Set canvas size to the specified dimensions
    canvas.width = coordinates.maxX - coordinates.minX;
    canvas.height = coordinates.maxY - coordinates.minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      coordinates.minX, coordinates.minY,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY,
      0, 0,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY
    );

    return canvas.toDataURL('image/png');
  };

  // Process image and run OCR
  const handleRunOCR = () => {
    if (!images[currentImageIndex]) {
      alert('Please upload an image first');
      return;
    }

    setIsLoading(true);

    const img = new Image();
    img.src = images[currentImageIndex].url;

    img.onload = () => {
      const croppedImageData = cropImage(img);

      Tesseract.recognize(
        croppedImageData,
        'eng',
        {
          logger: (m) => console.log(m)
        }
      )
        .then(({ data: { text } }) => {
          setText(text);
          // Update the text in the images array
          setImages(prev => prev.map((img, index) => 
            index === currentImageIndex ? { ...img, text, coordinates } : img
          ));
          setIsLoading(false);
        })
        .catch((error) => {
          console.error(error);
          setIsLoading(false);
        });
    };
  };

  // Handle coordinate changes for current image
  const handleCurrentImageCoordinatesChange = (e) => {
    const { name, value } = e.target;
    const newCoordinates = {
      ...coordinates,
      [name]: parseInt(value) || 0
    };
    setCoordinates(newCoordinates);
    // Update coordinates in the images array
    setImages(prev => prev.map((img, index) => 
      index === currentImageIndex ? { ...img, coordinates: newCoordinates } : img
    ));
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageChange}
        />
      </div>

      {/* Image Selection */}
      {images.length > 0 && (
        <div className="image-selection">
          <h3>Select Image:</h3>
          <div className="image-thumbnails">
            {images.map((img, index) => (
              <div 
                key={index}
                className={`image-thumbnail ${index === currentImageIndex ? 'selected' : ''}`}
                onClick={() => handleImageSelect(index)}
              >
                <img src={img.url} alt={`Thumbnail ${index + 1}`} />
                <span>Image {index + 1}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Coordinate Inputs */}
      {images.length > 0 && (
        <div className="coordinates-input">
          <h3>Enter Coordinates</h3>
          <div className="coordinate-group">
            <div>
              <label>Min X:</label>
              <input
                type="number"
                name="minX"
                value={coordinates.minX}
                onChange={handleCurrentImageCoordinatesChange}
                placeholder="Min X"
              />
            </div>
            <div>
              <label>Max X:</label>
              <input
                type="number"
                name="maxX"
                value={coordinates.maxX}
                onChange={handleCurrentImageCoordinatesChange}
                placeholder="Max X"
              />
            </div>
            <div>
              <label>Min Y:</label>
              <input
                type="number"
                name="minY"
                value={coordinates.minY}
                onChange={handleCurrentImageCoordinatesChange}
                placeholder="Min Y"
              />
            </div>
            <div>
              <label>Max Y:</label>
              <input
                type="number"
                name="maxY"
                value={coordinates.maxY}
                onChange={handleCurrentImageCoordinatesChange}
                placeholder="Max Y"
              />
            </div>
          </div>
        </div>
      )}

      {/* Run OCR Button */}
      {images.length > 0 && (
        <div className="run-ocr">
          <button onClick={handleRunOCR} disabled={isLoading}>
            {isLoading ? 'Processing...' : 'Run OCR'}
          </button>
        </div>
      )}

      {/* Image Previews Container */}
      {images.length > 0 && (
        <div className="image-preview-container">
          {/* Original Image with Rectangle */}
          <div className="image-preview">
            <h2>Original Image:</h2>
            <canvas ref={canvasRef} style={{ maxWidth: '400px' }} />
            <div className="image-dimensions">
              Size: {imageDimensions.width} x {imageDimensions.height} pixels
            </div>
          </div>

          {/* Cropped Image Preview */}
          {images[currentImageIndex].text && (
            <div className="image-preview">
              <h2>Cropped Image:</h2>
              <img 
                src={cropImage(new Image(images[currentImageIndex].url))} 
                alt="Cropped" 
                style={{ maxWidth: '400px' }} 
              />
            </div>
          )}

          {/* Extracted Text */}
          {images[currentImageIndex].text && (
            <div className="result">
              <h2>Extracted Text:</h2>
              <pre>{images[currentImageIndex].text}</pre>
            </div>
          )}
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}
    </div>
  );
}

export default App;
