// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [images, setImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [batchProgress, setBatchProgress] = useState({ current: 0, total: 0 });
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  const [coordinates, setCoordinates] = useState({
    minX: 0,
    maxX: 0,
    minY: 0,
    maxY: 0
  });
  const [currentRegionIndex, setCurrentRegionIndex] = useState(0);
  const canvasRef = useRef(null);

  // Draw rectangles on image
  const drawRectangles = (img) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size to match image
    canvas.width = img.width;
    canvas.height = img.height;

    // Draw the image
    ctx.drawImage(img, 0, 0);

    // Get current image
    const currentImage = images[currentImageIndex];
    if (!currentImage) return;

    // Draw all regions with different colors
    if (currentImage.regions && currentImage.regions.length > 0) {
      currentImage.regions.forEach((region, index) => {
        // Different color for current region
        ctx.strokeStyle = index === currentRegionIndex ? 'red' : 'blue';
        ctx.lineWidth = 2;
        ctx.strokeRect(
          region.coordinates.minX,
          region.coordinates.minY,
          region.coordinates.maxX - region.coordinates.minX,
          region.coordinates.maxY - region.coordinates.minY
        );

        // Add region number label
        ctx.fillStyle = index === currentRegionIndex ? 'red' : 'blue';
        ctx.font = '14px Arial';
        ctx.fillText(`Region ${index + 1}`, region.coordinates.minX, region.coordinates.minY - 5);
      });
    }
  };

  // Update rectangles when coordinates or regions change
  useEffect(() => {
    if (images[currentImageIndex]) {
      const img = new Image();
      img.src = images[currentImageIndex].url;
      img.onload = () => {
        drawRectangles(img);
        setImageDimensions({ width: img.width, height: img.height });
      };
    }
  }, [coordinates, currentImageIndex, images, currentRegionIndex]);

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files) {
      const newImages = Array.from(e.target.files).map(file => ({
        url: URL.createObjectURL(file),
        regions: [{
          coordinates: { minX: 0, maxX: 0, minY: 0, maxY: 0 },
          text: '',
          croppedImageData: null
        }]
      }));
      setImages(prev => [...prev, ...newImages]);
      setCurrentImageIndex(prev => prev + newImages.length - 1);
      setCurrentRegionIndex(0);
    }
  };

  // Handle image selection
  const handleImageSelect = (index) => {
    setCurrentImageIndex(index);
    setCurrentRegionIndex(0);

    // Update coordinates based on the first region of selected image
    if (images[index].regions && images[index].regions.length > 0) {
      setCoordinates(images[index].regions[0].coordinates);
    }

    // Update text if available
    if (images[index].regions && images[index].regions[0].text) {
      setText(images[index].regions[0].text);
    } else {
      setText('');
    }
  };

  // Add a new region
  const addRegion = () => {
    const newRegion = {
      coordinates: { minX: 0, maxX: 0, minY: 0, maxY: 0 },
      text: '',
      croppedImageData: null
    };

    // Use a single state update to avoid race conditions
    setImages(prev => {
      const newImages = [...prev];
      const currentImg = newImages[currentImageIndex];

      // Add the new region
      currentImg.regions.push(newRegion);

      // Update the current region index in a separate effect
      // This ensures we update indexes after the image state is updated
      setTimeout(() => {
        setCurrentRegionIndex(currentImg.regions.length - 1);
        setCoordinates({ minX: 0, maxX: 0, minY: 0, maxY: 0 });
        setText('');
      }, 0);

      return newImages;
    });
  };

  // Delete current region
  const deleteRegion = () => {
    // Don't allow deleting if only one region
    if (images[currentImageIndex].regions.length <= 1) {
      alert("Can't delete the only region. At least one region is required.");
      return;
    }

    const regionIndexToDelete = currentRegionIndex;

    // First update the images array
    setImages(prev => {
      const newImages = [...prev];
      // Save a copy of the regions before modification
      const updatedRegions = [...newImages[currentImageIndex].regions];
      updatedRegions.splice(regionIndexToDelete, 1);
      newImages[currentImageIndex].regions = updatedRegions;
      return newImages;
    });

    // Then update the current region index
    setCurrentRegionIndex(prev => {
      // If we're deleting the last region, go to the previous one
      if (prev >= images[currentImageIndex].regions.length - 1) {
        return Math.max(0, images[currentImageIndex].regions.length - 2);
      }
      // Otherwise stay on the same index (which now points to the next region)
      return prev;
    });

    // Update coordinates and text in next render cycle
    setTimeout(() => {
      if (images[currentImageIndex].regions.length > 0) {
        const newRegionIndex = Math.min(currentRegionIndex, images[currentImageIndex].regions.length - 1);
        if (images[currentImageIndex].regions[newRegionIndex]) {
          setCoordinates(images[currentImageIndex].regions[newRegionIndex].coordinates);
          setText(images[currentImageIndex].regions[newRegionIndex].text || '');
        }
      }
    }, 0);
  };

  // Handle region selection
  const handleRegionSelect = (index) => {
    setCurrentRegionIndex(index);
    const region = images[currentImageIndex].regions[index];
    setCoordinates(region.coordinates);
    setText(region.text || '');
  };

  // Crop image using coordinates
  const cropImage = (imageElement, coords) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Set canvas size to the specified dimensions
    canvas.width = coords.maxX - coords.minX;
    canvas.height = coords.maxY - coords.minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      coords.minX, coords.minY,
      coords.maxX - coords.minX, coords.maxY - coords.minY,
      0, 0,
      coords.maxX - coords.minX, coords.maxY - coords.minY
    );

    return canvas.toDataURL('image/png');
  };

  // Process single region with OCR
  const processRegionOCR = (imageUrl, regionCoords) => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.src = imageUrl;

      img.onload = () => {
        const croppedImageData = cropImage(img, regionCoords);

        Tesseract.recognize(
          croppedImageData,
          'eng',
          {
            logger: (m) => console.log(m)
          }
        )
          .then(({ data: { text } }) => {
            resolve({ text, croppedImageData });
          })
          .catch(reject);
      };
    });
  };

  // Process image and run OCR for current region
  const handleRunOCR = () => {
    if (!images[currentImageIndex]) {
      alert('Please upload an image first');
      return;
    }

    setIsLoading(true);
    processRegionOCR(images[currentImageIndex].url, coordinates)
      .then(({ text, croppedImageData }) => {
        setText(text);
        setImages(prev => {
          const newImages = [...prev];
          newImages[currentImageIndex].regions[currentRegionIndex] = {
            ...newImages[currentImageIndex].regions[currentRegionIndex],
            text,
            coordinates,
            croppedImageData
          };
          return newImages;
        });
        setIsLoading(false);
      })
      .catch(error => {
        console.error(error);
        setIsLoading(false);
      });
  };

  // Process all regions in current image
  const handleRunOCRAllRegions = async () => {
    if (!images[currentImageIndex]) {
      alert('Please upload an image first');
      return;
    }

    const regions = images[currentImageIndex].regions;
    if (regions.length === 0) {
      alert('No regions defined');
      return;
    }

    setIsLoading(true);
    setBatchProgress({ current: 0, total: regions.length });

    try {
      const updatedRegions = await Promise.all(
        regions.map(async (region, idx) => {
          const { text, croppedImageData } = await processRegionOCR(
            images[currentImageIndex].url, 
            region.coordinates
          );
          setBatchProgress(prev => ({ ...prev, current: idx + 1 }));
          return { ...region, text, croppedImageData };
        })
      );

      setImages(prev => {
        const newImages = [...prev];
        newImages[currentImageIndex].regions = updatedRegions;
        return newImages;
      });

      setText(updatedRegions[currentRegionIndex].text);
      setIsLoading(false);
      setBatchProgress({ current: 0, total: 0 });
    } catch (error) {
      console.error(error);
      setIsLoading(false);
      setBatchProgress({ current: 0, total: 0 });
    }
  };

  // Process all images with OCR (all regions)
  const handleRunOCRAll = async () => {
    if (images.length === 0) {
      alert('Please upload images first');
      return;
    }

    setIsLoading(true);
    let totalRegions = 0;
    images.forEach(img => totalRegions += img.regions.length);
    setBatchProgress({ current: 0, total: totalRegions });

    let processedCount = 0;

    try {
      const updatedImages = await Promise.all(
        images.map(async (img) => {
          const updatedRegions = await Promise.all(
            img.regions.map(async (region) => {
              const { text, croppedImageData } = await processRegionOCR(
                img.url, 
                region.coordinates
              );
              processedCount++;
              setBatchProgress({ current: processedCount, total: totalRegions });
              return { ...region, text, croppedImageData };
            })
          );
          return { ...img, regions: updatedRegions };
        })
      );

      setImages(updatedImages);
      if (updatedImages[currentImageIndex]?.regions[currentRegionIndex]?.text) {
        setText(updatedImages[currentImageIndex].regions[currentRegionIndex].text);
      }
      setIsLoading(false);
      setBatchProgress({ current: 0, total: 0 });
    } catch (error) {
      console.error(error);
      setIsLoading(false);
      setBatchProgress({ current: 0, total: 0 });
    }
  };

  // Handle coordinate changes for current region
  const handleCurrentRegionCoordinatesChange = (e) => {
    const { name, value } = e.target;
    const newCoordinates = {
      ...coordinates,
      [name]: parseInt(value) || 0
    };
    setCoordinates(newCoordinates);

    // Update coordinates in the images array
    setImages(prev => {
      const newImages = [...prev];
      if (newImages[currentImageIndex].regions[currentRegionIndex]) {
        newImages[currentImageIndex].regions[currentRegionIndex].coordinates = newCoordinates;
      }
      return newImages;
    });
  };

  // Export results as CSV
  const exportToCSV = () => {
    // Create CSV header
    let csvContent = "Image Name,Region,OCR Result\n";

    // Add each image and region data as rows
    images.forEach((img, imgIndex) => {
      img.regions.forEach((region, regionIndex) => {
        // Clean text data by escaping quotes and removing line breaks
        const cleanedText = region.text ? region.text.replace(/"/g, '""').replace(/\n/g, ' ') : '';
        // Add row to CSV
        csvContent += `"Image ${imgIndex + 1}","Region ${regionIndex + 1}","${cleanedText}"\n`;
      });
    });

    // Create a blob with the CSV content
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);

    // Create a link element to trigger download
    const link = document.createElement('a');
    link.setAttribute('href', url);
    link.setAttribute('download', 'ocr_results.csv');
    link.style.visibility = 'hidden';

    // Add to document, click to download, then remove
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageChange}
        />
      </div>

      {/* Image Selection */}
      {images.length > 0 && (
        <div className="image-selection">
          <h3>Select Image:</h3>
          <div className="image-thumbnails">
            {images.map((img, index) => (
              <div 
                key={index}
                className={`image-thumbnail ${index === currentImageIndex ? 'selected' : ''}`}
                onClick={() => handleImageSelect(index)}
              >
                <img src={img.url} alt={`Thumbnail ${index + 1}`} />
                <span>Image {index + 1}</span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Region Selection */}
      {images.length > 0 && images[currentImageIndex]?.regions?.length > 0 && (
        <div className="region-selection">
          <h3>Regions:</h3>
          <div className="region-controls">
            <button onClick={addRegion} className="control-button">Add Region</button>
            <button onClick={deleteRegion} className="control-button">Delete Region</button>
          </div>
          <div className="region-tabs">
            {images[currentImageIndex].regions.map((region, index) => (
              <div 
                key={index}
                className={`region-tab ${index === currentRegionIndex ? 'selected' : ''}`}
                onClick={() => handleRegionSelect(index)}
              >
                Region {index + 1}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Coordinate Inputs */}
      {images.length > 0 && (
        <div className="coordinates-input">
          <h3>Enter Coordinates for Region {currentRegionIndex + 1}</h3>
          <div className="coordinate-group">
            <div>
              <label>Min X:</label>
              <input
                type="number"
                name="minX"
                value={coordinates.minX}
                onChange={handleCurrentRegionCoordinatesChange}
                placeholder="Min X"
              />
            </div>
            <div>
              <label>Max X:</label>
              <input
                type="number"
                name="maxX"
                value={coordinates.maxX}
                onChange={handleCurrentRegionCoordinatesChange}
                placeholder="Max X"
              />
            </div>
            <div>
              <label>Min Y:</label>
              <input
                type="number"
                name="minY"
                value={coordinates.minY}
                onChange={handleCurrentRegionCoordinatesChange}
                placeholder="Min Y"
              />
            </div>
            <div>
              <label>Max Y:</label>
              <input
                type="number"
                name="maxY"
                value={coordinates.maxY}
                onChange={handleCurrentRegionCoordinatesChange}
                placeholder="Max Y"
              />
            </div>
          </div>
        </div>
      )}

      {/* OCR Buttons */}
      {images.length > 0 && (
        <div className="run-ocr">
          <button onClick={handleRunOCR} disabled={isLoading}>
            {isLoading ? 'Processing...' : 'Run OCR on Current Region'}
          </button>
          <button onClick={handleRunOCRAllRegions} disabled={isLoading} className="run-regions-button">
            {isLoading ? 'Processing...' : 'Run OCR on All Regions'}
          </button>
          <button onClick={handleRunOCRAll} disabled={isLoading} className="run-all-button">
            {isLoading ? `Processing ${batchProgress.current}/${batchProgress.total}...` : 'Run OCR on All Images'}
          </button>
        </div>
      )}

      {/* Image Previews Container */}
      {images.length > 0 && (
        <div className="image-preview-container">
          {/* Original Image with Rectangle */}
          <div className="image-preview">
            <h2>Original Image:</h2>
            <canvas ref={canvasRef} style={{ maxWidth: '400px' }} />
            <div className="image-dimensions">
              Size: {imageDimensions.width} x {imageDimensions.height} pixels
            </div>
          </div>

          {/* Cropped Image Preview */}
          {images[currentImageIndex]?.regions?.[currentRegionIndex]?.croppedImageData && (
            <div className="image-preview">
              <h2>Cropped Region {currentRegionIndex + 1}:</h2>
              <img 
                src={images[currentImageIndex].regions[currentRegionIndex].croppedImageData} 
                alt="Cropped" 
                style={{ maxWidth: '400px' }} 
              />
            </div>
          )}

          {/* Extracted Text */}
          {images[currentImageIndex]?.regions?.[currentRegionIndex]?.text && (
            <div className="result">
              <h2>Extracted Text from Region {currentRegionIndex + 1}:</h2>
              <pre>{images[currentImageIndex].regions[currentRegionIndex].text}</pre>
            </div>
          )}
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}

      {/* Export Button */}
      {images.length > 0 && images.some(img => img.regions && img.regions.some(region => region.text)) && (
        <div className="export-container">
          <button onClick={exportToCSV} className="export-button">
            Export Results as CSV
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
