// src/App.js
import React, { useState } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [coordinates, setCoordinates] = useState({
    x1: 0,
    y1: 0,
    x2: 0,
    y2: 0,
    x3: 0,
    y3: 0,
    x4: 0,
    y4: 0
  });

  // Handle coordinate changes
  const handleCoordinateChange = (e) => {
    const { name, value } = e.target;
    setCoordinates(prev => ({
      ...prev,
      [name]: parseInt(value) || 0
    }));
  };

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImage(URL.createObjectURL(file));
      processImage(file);
    }
  };

  // Crop image using coordinates
  const cropImage = (imageElement) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Calculate the bounding box of the quadrilateral
    const minX = Math.min(coordinates.x1, coordinates.x2, coordinates.x3, coordinates.x4);
    const maxX = Math.max(coordinates.x1, coordinates.x2, coordinates.x3, coordinates.x4);
    const minY = Math.min(coordinates.y1, coordinates.y2, coordinates.y3, coordinates.y4);
    const maxY = Math.max(coordinates.y1, coordinates.y2, coordinates.y3, coordinates.y4);

    // Set canvas size to the bounding box
    canvas.width = maxX - minX;
    canvas.height = maxY - minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      minX, minY,
      maxX - minX, maxY - minY,
      0, 0,
      maxX - minX, maxY - minY
    );

    return canvas.toDataURL('image/png');
  };

  // Process image with Tesseract
  const processImage = (file) => {
    setIsLoading(true);
    setText('');

    const img = new Image();
    img.src = URL.createObjectURL(file);

    img.onload = () => {
      const croppedImageData = cropImage(img);

      Tesseract.recognize(
        croppedImageData,
        'eng',
        {
          logger: (m) => console.log(m)
        }
      )
        .then(({ data: { text } }) => {
          setText(text);
          setIsLoading(false);
        })
        .catch((error) => {
          console.error(error);
          setIsLoading(false);
        });
    };
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
        />
      </div>

      {/* Coordinate Inputs */}
      <div className="coordinates-input">
        <h3>Enter Coordinates</h3>
        <div className="coordinate-group">
          <div>
            <label>Point 1:</label>
            <input
              type="number"
              name="x1"
              value={coordinates.x1}
              onChange={handleCoordinateChange}
              placeholder="X1"
            />
            <input
              type="number"
              name="y1"
              value={coordinates.y1}
              onChange={handleCoordinateChange}
              placeholder="Y1"
            />
          </div>
          <div>
            <label>Point 2:</label>
            <input
              type="number"
              name="x2"
              value={coordinates.x2}
              onChange={handleCoordinateChange}
              placeholder="X2"
            />
            <input
              type="number"
              name="y2"
              value={coordinates.y2}
              onChange={handleCoordinateChange}
              placeholder="Y2"
            />
          </div>
          <div>
            <label>Point 3:</label>
            <input
              type="number"
              name="x3"
              value={coordinates.x3}
              onChange={handleCoordinateChange}
              placeholder="X3"
            />
            <input
              type="number"
              name="y3"
              value={coordinates.y3}
              onChange={handleCoordinateChange}
              placeholder="Y3"
            />
          </div>
          <div>
            <label>Point 4:</label>
            <input
              type="number"
              name="x4"
              value={coordinates.x4}
              onChange={handleCoordinateChange}
              placeholder="X4"
            />
            <input
              type="number"
              name="y4"
              value={coordinates.y4}
              onChange={handleCoordinateChange}
              placeholder="Y4"
            />
          </div>
        </div>
      </div>

      {/* Preview Image */}
      {image && (
        <div className="image-preview">
          <img src={image} alt="Uploaded" style={{ maxWidth: '400px' }} />
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}

      {/* Extracted Text */}
      {text && (
        <div className="result">
          <h2>Extracted Text:</h2>
          <pre>{text}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
