// src/App.js
import React, { useState } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [croppedImage, setCroppedImage] = useState(null);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [coordinates, setCoordinates] = useState({
    minX: 0,
    maxX: 0,
    minY: 0,
    maxY: 0
  });

  // Handle coordinate changes
  const handleCoordinateChange = (e) => {
    const { name, value } = e.target;
    setCoordinates(prev => ({
      ...prev,
      [name]: parseInt(value) || 0
    }));
  };

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
      setCroppedImage(null); // Reset cropped image
      setText(''); // Reset text
    }
  };

  // Crop image using coordinates
  const cropImage = (imageElement) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Set canvas size to the specified dimensions
    canvas.width = coordinates.maxX - coordinates.minX;
    canvas.height = coordinates.maxY - coordinates.minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      coordinates.minX, coordinates.minY,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY,
      0, 0,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY
    );

    return canvas.toDataURL('image/png');
  };

  // Process image and run OCR
  const handleRunOCR = () => {
    if (!image) {
      alert('Please upload an image first');
      return;
    }

    setIsLoading(true);
    setText('');

    const img = new Image();
    img.src = image;

    img.onload = () => {
      const croppedImageData = cropImage(img);
      setCroppedImage(croppedImageData);

      Tesseract.recognize(
        croppedImageData,
        'eng',
        {
          logger: (m) => console.log(m)
        }
      )
        .then(({ data: { text } }) => {
          setText(text);
          setIsLoading(false);
        })
        .catch((error) => {
          console.error(error);
          setIsLoading(false);
        });
    };
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
        />
      </div>

      {/* Coordinate Inputs */}
      <div className="coordinates-input">
        <h3>Enter Coordinates</h3>
        <div className="coordinate-group">
          <div>
            <label>Min X:</label>
            <input
              type="number"
              name="minX"
              value={coordinates.minX}
              onChange={handleCoordinateChange}
              placeholder="Min X"
            />
          </div>
          <div>
            <label>Max X:</label>
            <input
              type="number"
              name="maxX"
              value={coordinates.maxX}
              onChange={handleCoordinateChange}
              placeholder="Max X"
            />
          </div>
          <div>
            <label>Min Y:</label>
            <input
              type="number"
              name="minY"
              value={coordinates.minY}
              onChange={handleCoordinateChange}
              placeholder="Min Y"
            />
          </div>
          <div>
            <label>Max Y:</label>
            <input
              type="number"
              name="maxY"
              value={coordinates.maxY}
              onChange={handleCoordinateChange}
              placeholder="Max Y"
            />
          </div>
        </div>
      </div>

      {/* Run OCR Button */}
      {image && (
        <div className="run-ocr">
          <button onClick={handleRunOCR} disabled={isLoading}>
            {isLoading ? 'Processing...' : 'Run OCR'}
          </button>
        </div>
      )}

      {/* Preview Image */}
      {image && (
        <div className="image-preview">
          <h3>Original Image:</h3>
          <img src={image} alt="Original" style={{ maxWidth: '400px' }} />
        </div>
      )}

      {/* Cropped Image Preview */}
      {croppedImage && (
        <div className="image-preview">
          <h3>Cropped Image:</h3>
          <img src={croppedImage} alt="Cropped" style={{ maxWidth: '400px' }} />
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}

      {/* Extracted Text */}
      {text && (
        <div className="result">
          <h2>Extracted Text:</h2>
          <pre>{text}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
