// src/App.js
import React, { useState } from 'react';
import Tesseract from 'tesseract.js';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [coordinates, setCoordinates] = useState({
    minX: 0,
    maxX: 0,
    minY: 0,
    maxY: 0
  });

  // Handle coordinate changes
  const handleCoordinateChange = (e) => {
    const { name, value } = e.target;
    setCoordinates(prev => ({
      ...prev,
      [name]: parseInt(value) || 0
    }));
  };

  // Handle image upload
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImage(URL.createObjectURL(file));
      processImage(file);
    }
  };

  // Crop image using coordinates
  const cropImage = (imageElement) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // Set canvas size to the specified dimensions
    canvas.width = coordinates.maxX - coordinates.minX;
    canvas.height = coordinates.maxY - coordinates.minY;

    // Draw the cropped region
    ctx.drawImage(
      imageElement,
      coordinates.minX, coordinates.minY,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY,
      0, 0,
      coordinates.maxX - coordinates.minX, coordinates.maxY - coordinates.minY
    );

    return canvas.toDataURL('image/png');
  };

  // Process image with Tesseract
  const processImage = (file) => {
    setIsLoading(true);
    setText('');

    const img = new Image();
    img.src = URL.createObjectURL(file);

    img.onload = () => {
      const croppedImageData = cropImage(img);

      Tesseract.recognize(
        croppedImageData,
        'eng',
        {
          logger: (m) => console.log(m)
        }
      )
        .then(({ data: { text } }) => {
          setText(text);
          setIsLoading(false);
        })
        .catch((error) => {
          console.error(error);
          setIsLoading(false);
        });
    };
  };

  return (
    <div className="App">
      <h1>OCR Image to Text</h1>

      {/* Image Upload */}
      <div>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
        />
      </div>

      {/* Coordinate Inputs */}
      <div className="coordinates-input">
        <h3>Enter Coordinates</h3>
        <div className="coordinate-group">
          <div>
            <label>Min X:</label>
            <input
              type="number"
              name="minX"
              value={coordinates.minX}
              onChange={handleCoordinateChange}
              placeholder="Min X"
            />
          </div>
          <div>
            <label>Max X:</label>
            <input
              type="number"
              name="maxX"
              value={coordinates.maxX}
              onChange={handleCoordinateChange}
              placeholder="Max X"
            />
          </div>
          <div>
            <label>Min Y:</label>
            <input
              type="number"
              name="minY"
              value={coordinates.minY}
              onChange={handleCoordinateChange}
              placeholder="Min Y"
            />
          </div>
          <div>
            <label>Max Y:</label>
            <input
              type="number"
              name="maxY"
              value={coordinates.maxY}
              onChange={handleCoordinateChange}
              placeholder="Max Y"
            />
          </div>
        </div>
      </div>

      {/* Preview Image */}
      {image && (
        <div className="image-preview">
          <img src={image} alt="Uploaded" style={{ maxWidth: '400px' }} />
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && <p>Processing image...</p>}

      {/* Extracted Text */}
      {text && (
        <div className="result">
          <h2>Extracted Text:</h2>
          <pre>{text}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
